var _ = require('underscore');

// Create module
module.exports = {
    
    // Create employees array with some data
    employees: [
        {
            id: 1,
            name: 'Donald Duck'            
        },
        {
            id: 2,
            name: 'Mickey Mouse'            
        }
    ],
    
    // Function to get an employee by id
    getEmployeeById: function(id) {
        return _.where(this.employees, { 'id': id })[0];
    }    
};
